import React, {useState, useContext, useEffect} from 'react'
import axios from 'axios'
import {GlobalState} from '../../../GlobalState'
import Loading from '../utils/loading/Loading'
import {useNavigate, useParams} from 'react-router-dom'

const initialState = {
    product_id: '',
    title: '',
    price: 0,
    description: '',
    content: '',
    category: '',
    _id: ''
}

function CreateProduct() {
    const state = useContext(GlobalState)
    const [product, setProduct] = useState(initialState)
    const [categories] = state.categoriesAPI.categories
    const [images, setImages] = useState(false)
    const [loading, setLoading] = useState(false)


    const [isAdmin] = state.userAPI.isAdmin
    const [token] = state.token

    const navigate = useNavigate()
    const param = useParams()

    const [products] = state.productsAPI.products
    const [onEdit, setOnEdit] = useState(false)
    const [callback, setCallback] = state.productsAPI.callback

    useEffect(() => {
        if(param.id){
            setOnEdit(true)
            products.forEach(product => {
                if(product._id === param.id) {
                    setProduct(product)
                    setImages(product.images)
                }
            })
        }else{
            setOnEdit(false)
            setProduct(initialState)
            setImages(false)
        }
    }, [param.id, products])

    const handleUpload = async (e) => {
        e.preventDefault();
        try {
            if (!isAdmin) return alert("You're not an admin");
    
            const file = e.target.files[0];
    
            if (!file) return alert("File not exist.");
    
            if (file.size > 1024 * 1024) // 1mb
                return alert("Size too large!");
    
            if (file.type !== 'image/jpeg' && file.type !== 'image/png') // 1mb
                return alert("File format is incorrect.");
    
            let formData = new FormData();
            formData.append('file', file);
    
            setLoading(true);
    
            const res = await axios.post('/api/upload', formData, {
                headers: {
                    'content-type': 'multipart/form-data',
                    Authorization: token
                }
            });
    
            setLoading(false);
            setImages(res.data);
        } catch (err) {
            if (err.response) {
                alert(`Error: ${err.response.data.msg}`);
            } else {
                alert(`Error: ${err.message}`);
            }
        }
    };

    const handleDestroy = async () => {
        try {
            if(!isAdmin) return alert("You're not an admin");
            setLoading(true);
            const response = await fetch('/api/destroy', {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': token
                },
                body: JSON.stringify({public_id: images.public_id})
            });
    
            if (!response.ok) {
                throw new Error('Response is not OK');
            }
    
            setLoading(false);
            setImages(false);
        } catch (err) {
            alert(err.message);
        }
    }
    

    const handleChangeInput = e =>{
        const {name, value} = e.target
        setProduct({...product, [name]:value})
    }

    const handleSubmit = async e => {
        e.preventDefault();
        try {
            if(!isAdmin) return alert("You're not an admin");
            if(!images) return alert("No Image Upload");
    
            const requestOptions = {
                method: onEdit ? 'PUT' : 'POST',
                headers: { 
                    'Content-Type': 'application/json',
                    'Authorization': token
                },
                body: JSON.stringify({...product, images})
            };
    
            const response = await fetch(onEdit ? `/api/products/${product._id}` : '/api/products', requestOptions);
    
            if (!response.ok) {
                throw new Error('Response is not OK');
            }
    
            setCallback(!callback);
            navigate("/");
        } catch (err) {
            alert(err.message);
        }
    }
    

    const styleUpload = {
        display: images ? "block" : "none"
    }
    return (
        <div className="create_product">
            <div class="container">
                <div class="row">
                    <div class="col-lg-6 col-md-6">
                        <div class="row">
                            <div class="col-lg-6 col-md-6">
                                <div className="upload">
                                    <input type="file" name="image1" id="file_up" onChange={handleUpload}/>
                                    {
                                        loading ? <div id="file_img"><Loading /></div>
                                        :<div id="file_img" style={styleUpload}>
                                            <img src={images ? images.url : ''} alt=""/>
