import React, {useState, useContext} from 'react'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'
import './categories.css'

function Categories() {
    const state = useContext(GlobalState)
    const [categories] = state.categoriesAPI.categories
    const [category, setCategory] = useState('')
    const [group, setGroup] = useState('')
    const [token] = state.token
    const [callback, setCallback] = state.categoriesAPI.callback
    const [onEdit, setOnEdit] = useState(false)
    const [id, setID] = useState('')

    const createCategory = async e =>{
        e.preventDefault()
        try {
            if(onEdit){
                const res = await axios.put(`/api/category/${id}`, {name: category, group: group}, {
                    headers: {Authorization: token}
                })
                alert(res.data.msg)
            }else{
                const res = await axios.post('/api/category', {name: category, group: group}, {
                    headers: {Authorization: token}
                })
                alert(res.data.msg)
            }
            setOnEdit(false)
            setCategory('')
            setGroup('')
            setCallback(!callback)
            
        } catch (err) {
            alert(err.response.data.msg)
        }
    }

    const editCategory = async (id, name, group) =>{
        setID(id)
        setCategory(name)
        setGroup(group)
        setOnEdit(true)
    }

    const deleteCategory = async id =>{
        try {
            const res = await fetch(`/api/category/${id}`, {
                method: 'DELETE',
                headers: { 'Authorization': token }
            });
            
            alert(res.data.msg)
            setCallback(!callback)
        } catch (err) {
            alert(err.response.data.msg)
        }
    }

    return (
        <>
        <div className="categories">
            <form onSubmit={createCategory}>
                <label htmlFor="category">Category</label>
                <input type="text" name="category" value={category} required
                onChange={e => setCategory(e.target.value)} />

                <button type="submit">{onEdit? "Update" : "Create"}</button>
            </form>

            <div className="col">
                {
                    categories.map(category => (
                        <div className="row" key={category._id}>
                            <p>{category.name}</p>
                            <div>
                                <button onClick={() => editCategory(category._id, category.name)}>Edit</button>
                                <button onClick={() => deleteCategory(category._id)}>Delete</button>
                            </div>
                        </div>
                    ))
                }
            </div>
        </div>
        <div class="edit-categories">
            <div class="container">
                <div class="row">
                    <div class="col-lg-12 col-md-6">
                        <div class="group-categories">
                            <h4>Tạo danh mục</h4>
                            <form onSubmit={createCategory}>
                                <input type="text" name="category" value={category} required
                                placeholder="Tên danh mục" onChange={e => setCategory(e.target.value)} />
                                <input type="text" name="group" value={group} required
                                placeholder="Nhóm" onChange={e => setCategory(e.target.value)} />
                                <button type="submit">{onEdit? "Cập nhật" : "Tạo"}</button>
                            </form>
                        </div>
                    </div>
                    <div class="col-lg-6 col-md-6">
                        <div class="group-categories">
                            <h3>Áo</h3>
                        </div>
                    </div>
                    <div class="col-lg-6 col-md-6">
                        <div class="group-categories">
                            <h3>Quần</h3>
                        </div>
                    </div>
                    <div class="col-lg-6 col-md-6">
                        <div class="group-categories">
                            <h3>Váy</h3>
                        </div>
                    </div>
                    <div class="col-lg-6 col-md-6">
                        <div class="group-categories">
                            <h3>Phụ kiện</h3>
                        </div>
                    </div>
                </div>
            </div>
        </div> 
        </>
    )
}

export default Categories
